import React, { useContext, useEffect, useRef, useReducer, useState, createContext } from "react";
import * as d3 from 'd3'
import {SentimentContext} from '../App'
import {HoverContext} from '../App'


const NetSentiment = () =>{

    var data = [{name: "Puerto Rico", value: -0.14281404556189306}
    ,{name: "West Virginia", value: -0.032881380080021845}
    ,{name: "Illinois", value: -0.01237748849783861}
    ,{name: "Vermont", value: -0.0027998804617245794}
    ,{name: "Connecticut", value: -0.0024649582817701924}
    ,{name: "Mississippi", value: 0.0029831863814104216}]

    const d3Container = useRef(null)

    useEffect(()=>{

        d3.select(".root_netsentiment").selectAll('*').remove()

        var margin = {top: 20, right: 40, bottom: 60, left: 40},
        width = 500 - margin.left - margin.right,
        height = 200 - margin.top - margin.bottom;

        var x = d3.scaleBand()
        .domain(data.map(e => e.name))
        .rangeRound([0, width])
        .padding(0.4);

        var y = d3.scaleLinear()
        .range([height, 0])
        .domain([0,100]);



        var baseline = d3.axisBottom(x)
        .tickSize(0)
        .tickFormat('');

        var xAxis = d3.axisBottom(x)
        .tickSize(0);

        var yAxis = d3.axisLeft(y)
        .tickSize(0)
        .ticks(7)
        .tickFormat(function(d) { return parseInt(d) });

        // CHART
        var svg = d3.select(d3Container.current)
        .attr('class', 'root_netsentiment')
        .append("svg")
        .attr("width", width + margin.left + margin.right)
        .attr("height", height + margin.top + margin.bottom)


        var chart = svg.append('g')
        .attr('class', 'canvas')
        .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

        // add axis to chart


        // Ticks on x-axis and y-axis
    svg.append("g")
    .attr("class", "x axis")
    .attr("transform", "translate(0," + height + ")")
    .call(xAxis)
    //.selectAll(".tick text")
    //.call(wrap, x0.bandwidth())
    .selectAll("text")
    .style("text-anchor", "end")
    .attr("dx", "1.2em")
    .attr("dy", "1em")
    .attr('overflow', 'visible')
    .attr("transform", "rotate(-20)")
    //.style('text-decoration', 'underline')
    .attr('fill', '#4d4d4d')


    svg.append("g")
    .attr("class", "y0 axis")
    .call(yAxis)
    .append("text")
    .attr("transform", "rotate(-90)")
    .attr("y", 6)
    .attr("dy", ".71em")
    .style("text-anchor", "end")
    .style("fill", "#005282")
    .text("Engagement");


svg.select('.x axis')
.selectAll('text')
.style("fill","#07737F");


svg.select('.y .axis')
    .selectAll('.tick')
    .style("fill","#d0743c");
// End ticks




    }) // close useeffect

    return(
        <>
            <svg id='netSentiment' ref={d3Container}></svg>

        </>
    )
}

export default NetSentiment
