import React, { useContext, useEffect, useRef, useReducer, useState, createContext } from "react";
import * as d3 from 'd3'
import ReactTooltip from 'react-tooltip';
import '../style/linechart.css'

const LineChart = () =>{

    const d3Container = useRef(null)

    const data =[
        {id:'pos', values:[
            {month:'jan', number: 20},
            {month:'feb', number: 20},
            {month:'mars', number: 20},
            {month:'april', number: 20},
            {month:'may', number: 20},
            {month:'june', number: 20},
        ]},
        {id:'neg', values:[
            {month:'jan', number: 30},
            {month:'feb', number: 30},
            {month:'mars', number: 30},
            {month:'april', number: 30},
            {month:'may', number: 30},
            {month:'june', number: 30},
        ]},
        {id:'neu', values:[
            {month:'jan', number: 50},
            {month:'feb', number: 50},
            {month:'mars', number: 50},
            {month:'april', number: 50},
            {month:'may', number: 50},
            {month:'june', number: 50},
        ]},

    ]

    const months = ['jan', 'feb', 'mars', 'april', 'may', 'june']

    useEffect(()=>{

    d3.select(".root_linechart").selectAll('*').remove()

    var keys = months

    var margin = {top: 15, right: 35, bottom: 15, left: 35},
        width = 500 - margin.left - margin.right,
        height = 250 - margin.top - margin.bottom;


    var x = d3.scaleBand()
        .domain(keys)
        .rangeRound([0, width])
        .padding(0.4);

    var y = d3.scaleLinear()
        .range([height, 0])
        .domain([0, 100]);


	var z = d3.scaleOrdinal(['#63A37B','#F15645', '#7D7D7D']);

    var line = d3.line()
		.curve(d3.curveCardinal)
		.x(d => x(d.month))
		.y(d => y(d.number));


    var xAxis = d3.axisBottom(x)
        .ticks(5);

    var yAxis = d3.axisLeft(y)
        // .scale(y1)
         .tickFormat(function(d) { return parseInt(d) })
         .tickSize(-width + margin.right + margin.left)


    var svg = d3.select(d3Container.current)
        .attr('class', 'root_linechart')
        .attr("width", width + margin.left + margin.right)
        .attr("height", height + margin.top + margin.bottom)

    svg.append("g")
		.attr("class","x-axis")
        .style('color', '#999')
		.attr("transform", "translate(0," + (height - margin.bottom) + ")")
		.call(xAxis);

	svg.append("g")
		.attr("class", "y-axis")
        .style('color', '#999')
		.attr("transform", "translate(" + margin.left + ",0)")
        .call(yAxis)



	var focus = svg.append("g")
		.attr("class", "focus")
		.style("display", "none");

	focus.append("line").attr("class", "lineHover")
		.style("stroke", "#999")
		.attr("stroke-width", 1)
		.style("shape-rendering", "crispEdges")
		.style("opacity", 0.5)
		.attr("y1", -height)
		.attr("y2",0);



	focus.append("text").attr("class", "lineHoverDate")
		.attr("text-anchor", "middle")
		.attr("font-size", 12);

	var overlay = svg.append("rect")
		.attr("class", "overlay")
		.attr("x", margin.left)
		.attr("width", width - margin.right - margin.left)
		.attr("height", height)

    var sentiment = svg.selectAll(".cities")
        .data(data);

    sentiment.exit().remove();

    sentiment.enter().insert("g", ".focus").append("path")
			.attr("class", "line cities")
			.style("stroke", d => z(d.id))
			.merge(sentiment)
		//.transition().duration(speed)
			.attr("d", d => line(d.values))



    })

    return(
        <>
            <svg id='lineChart' width="595" height="287" ref={d3Container}></svg>
        </>
    )
}

export default LineChart
