import React, { useContext, useEffect, useRef, useReducer, useState, createContext } from "react";
import * as d3 from 'd3'
import {SentimentContext} from '../App'
import {HoverContext} from '../App'
import '../style/netsentiment.css'


const NetSentiment = () =>{

    const SentSentimentContext = useContext(SentimentContext);
    const {choosenSentiment, setChoosenSentiment} = SentSentimentContext


    var post_data = []
    const handleData = (data) =>{
        if (!('children' in data)){
            post_data.push({
                value: (data.pos-data.neg)*data.comments, name:data.name
            })
        }
        else{
            data.children.forEach(child =>{
                handleData(child)
            })
        }
    }

    handleData(choosenSentiment)
    console.log(post_data)

    const d3Container = useRef(null)

    useEffect(()=>{

        d3.select(".root_netsentiment").selectAll('*').remove()

        var margin = {top: 30, right: 10, bottom: 50, left: 50},
        width = 350,
        height = 150;

        var data = [{value: 10, dataset:"barbaz"},
        {value: 40, dataset:"barbar"},
        {value: -10, dataset:"foobaz"},
        {value: 50, dataset:"foobar"},
        ];

        // Add svg to
        var svg = d3.select(d3Container.current)
        .attr('class', 'root_netsentiment')
        .append('svg').attr('width', width + margin.left + margin.right).attr('height', height + margin.top + margin.bottom).append('g').attr('transform', 'translate(' + margin.left + ',' + margin.top + ')');

        // set the ranges
        var y = d3.scaleBand()
            .range([height, 0])
            .padding(0.1);

        var x = d3.scaleLinear()
            //.range([0, width])
            .rangeRound([margin.left, width - margin.right])

        // Scale the range of the data in the domains
        x.domain(d3.extent(post_data, function (d) {return d.value;}));

        y.domain(post_data.map(function (d) {
            return d.name;
        }));

        var xAxis = d3.axisBottom(x)
        .ticks(5)

        var yAxis = d3.axisRight(y)

        // append the rectangles for the bar chart
        svg.selectAll(".bar")
            .data(post_data)
            .enter().append("rect")
            .attr("class", function (d) {
                return "bar bar--" + (d.value < 0 ? "negative" : "positive");
            })
            .attr("x", function (d) {
                return x(Math.min(0, d.value));
            })
            .attr("y", function (d) {
                return y(d.name);
            })
            .attr("width", function (d) {
                return Math.abs(x(d.value) - x(0));
            })
            .attr("height", y.bandwidth());

        // add the x Axis
        svg.append("g")
            .attr("class", "x axis")
            .attr("transform", "translate(0," + height + ")")
            .call(xAxis)
            .selectAll(".x.axis text")
            .style("fill","#4d4d4d")

        // add the y Axis
        svg.append("g")
            .attr("class", "y axis")
            .attr("transform", "translate(" + x(0) + ",0)")
            .call(yAxis)
            .selectAll(".y.axis text")
            .style("fill","#4d4d4d")



    }) // close useeffect

    return(
        <>
            <svg id='netSentiment' width={500} height={300} ref={d3Container}></svg>

        </>
    )
}

export default NetSentiment
