import React, { useContext, useEffect, useRef, useReducer, useState, createContext } from "react";
import * as d3 from 'd3'
import {SentimentContext} from '../App'
import {HoverContext} from '../App'


const NetSentiment = () =>{

    var data = [{name: "Puerto Rico", value: -0.14281404556189306}
    ,{name: "West Virginia", value: -0.032881380080021845}
    ,{name: "Illinois", value: -0.01237748849783861}
    ,{name: "Vermont", value: -0.0027998804617245794}
    ,{name: "Connecticut", value: -0.0024649582817701924}
    ,{name: "Mississippi", value: 0.0029831863814104216}]

    const d3Container = useRef(null)

    useEffect(()=>{

        var barHeight = 25
        var width = 200
        var height = Math.ceil((data.length + 0.1) * barHeight) + margin.top + margin.bottom
        var margin = ({top: 30, right: 60, bottom: 10, left: 60})

        //var tickFormat = metric === "absolute" ? d3.formatPrefix("+.1", 1e6) : format
        //var format = d3.format(metric === "absolute" ? "+,d" : "+,.0%")

        var x = d3.scaleLinear()
        .domain(d3.extent(data, d => d.value))
        .rangeRound([margin.left, width - margin.right])

        var y = d3.scaleBand()
        .domain(d3.range(data.length))
        .rangeRound([margin.top, height - margin.bottom])
        .padding(0.1)

        var xAxis = g => g
        .attr("transform", `translate(0,${margin.top})`)
        .call(d3.axisTop(x).ticks(width / 80).tickFormat(function(d) { return parseInt(d)}))
        .call(g => g.select(".domain").remove())

        var yAxis = g => g
        .attr("transform", `translate(${x(0)},0)`)
        .call(d3.axisLeft(y).tickFormat(i => data[i].name).tickSize(0).tickPadding(6))
        .call(g => g.selectAll(".tick text").filter(i => data[i].value < 0)
        .attr("text-anchor", "start")
        .attr("x", 6))

        // CHART

        const svg = d3.select(d3Container.current)
            .attr('class', 'root_netsentiment')
            .attr("viewBox", [0, 0, width, height])
            .append('svg')

        svg.append("g")
            .selectAll("rect")
            .data(data)
            .join("rect")
            .attr("fill", d => d3.schemeSet1[d.value > 0 ? 1 : 0])
            .attr("x", d => x(Math.min(d.value, 0)))
            .attr("y", (d, i) => y(i))
            .attr("width", d => Math.abs(x(d.value) - x(0)))
            .attr("height", y.bandwidth());

        svg.append("g")
            .attr("font-family", "sans-serif")
            .attr("font-size", 10)
            .selectAll("text")
            .data(data)
            .join("text")
            .attr("text-anchor", d => d.value < 0 ? "end" : "start")
            .attr("x", d => x(d.value) + Math.sign(d.value - 0) * 4)
            .attr("y", (d, i) => y(i) + y.bandwidth() / 2)
            .attr("dy", "0.35em")
            //.text(d => format(d.value));

        svg.append("g")
            .call(xAxis);

        svg.append("g")
            .call(yAxis);

        return svg.node();

    }) // close useeffect

    return(
        <>
            <svg id='netSentiment' ref={d3Container}></svg>

        </>
    )
}

export default NetSentiment
