import React, { useContext, useEffect, useRef, useReducer, useState, createContext } from "react";
import * as d3 from 'd3'
import ReactTooltip from 'react-tooltip';
import '../style/linechart.css'

const LineChart = () =>{

    const d3Container = useRef(null)

    const data = [
        {month:'jan', pos:20, neg:30, neu:50},
        {month:'feb', pos:20, neg:30, neu:50},
        {month:'mars', pos:20, neg:30, neu:50},
        {month:'april', pos:20, neg:30, neu:50},
        {month:'may', pos:20, neg:30, neu:50},
        {month:'june', pos:20, neg:30, neu:50},
    ]

    const months = ['jan', 'feb', 'mars', 'april', 'may', 'june']

    useEffect(()=>{

    d3.select(".root_linechart").selectAll('*').remove()

    var keys = months


	var svg = d3.select(d3Container.current).attr('class', 'root_linechart'),
		margin = {top: 15, right: 35, bottom: 15, left: 35},
		width = +svg.attr("width") - margin.left - margin.right,
		height = +svg.attr("height") - margin.top - margin.bottom;

	var x = d3.scaleTime()
		.rangeRound([margin.left, width - margin.right])
		.domain(keys)

    var y = d3.scaleLinear()
		.rangeRound([height - margin.bottom, margin.top]);

	var z = d3.scaleOrdinal(['#63A37B','#F15645', '#7D7D7D']);

	var line = d3.line()
		.curve(d3.curveCardinal)
		.x(d => x(d.date))
		.y(d => y(d.degrees));

    svg.append("g")
		.attr("class","x-axis")
        .style('color', '#999')
		.attr("transform", "translate(0," + (height - margin.bottom) + ")")
		.call(d3.axisBottom(x));

	svg.append("g")
		.attr("class", "y-axis")
    .style('color', '#999')
		.attr("transform", "translate(" + margin.left + ",0)");

	var focus = svg.append("g")
		.attr("class", "focus")
		.style("display", "none");

	focus.append("line").attr("class", "lineHover")
		.style("stroke", "#999")
		.attr("stroke-width", 1)
		.style("shape-rendering", "crispEdges")
		.style("opacity", 0.5)
		.attr("y1", -height)
		.attr("y2",0);

	focus.append("text").attr("class", "lineHoverDate")
		.attr("text-anchor", "middle")
		.attr("font-size", 12);

	var overlay = svg.append("rect")
		.attr("class", "overlay")
		.attr("x", margin.left)
		.attr("width", width - margin.right - margin.left)
		.attr("height", height)


    })

    return(
        <>
            <svg id='lineChart' width="595" height="287" ref={d3Container}></svg>
        </>
    )
}

export default LineChart
