import React, { useContext, useEffect, useRef, useReducer, useState, createContext } from "react";
import * as d3 from 'd3'
import ReactTooltip from 'react-tooltip';
import '../style/linechart.css'

const LineChart = () =>{

    const d3Container = useRef(null)

    const data =[
        {name:'pos', values:[
            {date:'jan', price: '20'},
            {date:'feb', price: '22'},
            {date:'mars', price: '15'},
            {date:'april', price: '28'},
            {date:'may', price: '20'},
            {date:'june', price: '23'},
        ]},
        {name:'neg', values:[
            {date:'jan', price: '31'},
            {date:'feb', price: '30'},
            {date:'mars', price: '36'},
            {date:'april', price: '33'},
            {date:'may', price: '28'},
            {date:'june', price: '30'},
        ]},
        {name:'neu', values:[
            {date:'jan', price: '54'},
            {date:'feb', price: '55'},
            {date:'mars', price: '57'},
            {date:'april', price: '53'},
            {date:'may', price: '52'},
            {date:'june', price: '59'},
        ]},

    ]

    const months = ['jan', 'feb', 'mars', 'april', 'may', 'june']

    useEffect(()=>{

    d3.select(".root_linechart").selectAll('*').remove()

    var width = 400;
var height = 200;
var margin = 30;
var duration = 250;

var lineOpacity = "0.25";
var lineOpacityHover = "0.85";
var otherLinesOpacityHover = "0.1";
var lineStroke = "1.5px";
var lineStrokeHover = "2.5px";

var circleOpacity = '0.85';
var circleOpacityOnLineHover = "0.25"
var circleRadius = 3;
var circleRadiusHover = 6;


/* Scale */
var xScale = d3.scalePoint()
  .domain(months)
  .rangeRound([0, width - margin])


var yScale = d3.scaleLinear()
  .domain([0, 100])
  .range([height-margin, 0]);

var color = d3.scaleOrdinal(['#63A37B','#F15645', '#7D7D7D']);

/* Add SVG */
var svg = d3.select(d3Container.current)
.attr('class', 'root_linechart')
.append("svg")
.attr("width", width + margin + margin)
.attr("height", height + margin + margin)
  .append('g')
  .attr("transform", "translate(" + margin + "," + margin + ")");

  var tooltip = d3.select('#tooltip');
  var tooltipLine = svg.append('line');


/* Add line into SVG */
var line = d3.line()
  .x(d => xScale(d.date))
  .y(d => yScale(d.price));

let lines = svg.append('g')
  .attr('class', 'lines');

lines.selectAll('.line-group')
  .data(data).enter()
  .append('g')
  .attr('class', 'line-group')  
  .on("mouseover", function(d, i) {
      svg.append("text")
        .attr("class", "title-text")
        .style("fill", color(i))        
        .text(d.name)
        .attr("text-anchor", "middle")
        .attr("x", (width-margin)/2)
        .attr("y", 5);
    })
  .on("mouseout", function(d) {
      svg.select(".title-text").remove();
    })
  .append('path')
  .attr('class', 'line')  
  .attr('d', d => line(d.values))
  .style('stroke', (d, i) => color(i))
  .style('opacity', lineOpacity)
  .on("mouseover", function(d) {
      d3.selectAll('.line')
					.style('opacity', otherLinesOpacityHover);
      d3.selectAll('.circle')
					.style('opacity', circleOpacityOnLineHover);
      d3.select(this)
        .style('opacity', lineOpacityHover)
        .style("stroke-width", lineStrokeHover)
        .style("cursor", "pointer");
    })
  .on("mouseout", function(d) {
      d3.selectAll(".line")
					.style('opacity', lineOpacity);
      d3.selectAll('.circle')
					.style('opacity', circleOpacity);
      d3.select(this)
        .style("stroke-width", lineStroke)
        .style("cursor", "none");
    });


/* Add circles in the line */
lines.selectAll("circle-group")
  .data(data).enter()
  .append("g")
  .style("fill", (d, i) => color(i))
  .selectAll("circle")
  .data(d => d.values).enter()
  .append("g")
  .attr("class", "circle")  
  .on("mouseover", 
  /*
  function(d) {
      d3.select(this)     
        .style("cursor", "pointer")
        .append("text")
        .attr("class", "text")
        .text(`${d.price}`)
        .attr("x", d => xScale(d.date) + 5)
        .attr("y", d => yScale(d.price) - 10);
    })
    */
  drawTooltip)
  .on("mouseout", function(d) {
      d3.select(this)
        .style("cursor", "none")  
        .transition()
        .duration(duration)
        .selectAll(".text").remove();
    })
  .append("circle")
  .attr("cx", d => xScale(d.date))
  .attr("cy", d => yScale(d.price))
  .attr("r", circleRadius)
  .style('opacity', circleOpacity)
  .on("mouseover", function(d) {
        d3.select(this)
          .transition()
          .duration(duration)
          .attr("r", circleRadiusHover);
      })
    .on("mouseout", function(d) {
        d3.select(this) 
          .transition()
          .duration(duration)
          .attr("r", circleRadius);  
      });


/* Add Axis into SVG */
var xAxis = d3.axisBottom(xScale).ticks(5);
var yAxis = d3.axisLeft(yScale).ticks(5);

svg.append("g")
  .attr("class", "x axis")
  .attr("transform", `translate(0, ${height-margin})`)
  .call(xAxis)

svg.append("g")
  .attr("class", "y axis")
  .call(yAxis)
  .append('text')
  .attr("y", 15)
  .attr("transform", "rotate(-90)")
  .attr("fill", "#000")
  .text("Total values");


  function drawTooltip() {

    function scalePointPosition() {
        var xPos = d3.mouse(this)[0];
        var domain = xScale.domain(); 
        var range = xScale.range();
        var rangePoints = d3.range(range[0], range[1], xScale.step())
        var yPos = domain[d3.bisect(rangePoints, xPos) -1];
        console.log(yPos);
    }

    //const year = scalePointPosition
    /*
    data.sort((a, b) => {
      return b.values.find(h => h.date == year).price - a.values.find(h => h.date == year).price;
    })  
    */

    tooltipLine.attr('stroke', 'black')
      .attr('x1', xScale('mars'))
      .attr('x2', xScale('mars'))
      .attr('y1', 0)
      .attr('y2', height);

    tooltip.html('mars')
      .style('display', 'block')
      .style('left', d3.event.pageX + 20)
      .style('top', d3.event.pageY - 20)
      .selectAll()
      .data(data).enter()
      .append('div')
      .style('color', d => d.color)
      //.html(d => d.name + ': ' + d.values.find(h => h.month == year).price);
  }

    })

    return(
        <>
            <svg id='lineChart' width="500" height="300" ref={d3Container}></svg>
        </>
    )
}

export default LineChart
