        handlePost()
    }) // close useeffect

    return(
        <>
            <div className='textInsightContainer' dangerouslySetInnerHTML=
            {{__html:text}}/>
            <img src={require(`../images/${img}.png`).default}></img>


        </>
    )
}

export default TextInsight;
