import React, { useContext, useEffect, useRef, useReducer, useState, createContext } from "react";
import * as d3 from 'd3'
import {SentimentContext} from '../App'
import {HoverContext} from '../App'


const NetSentiment = () =>{


    const d3Container = useRef(null)

    useEffect(()=>{

        d3.select(".root_netsentiment").selectAll('*').remove()

        var margin = {top: 30, right: 10, bottom: 50, left: 50},
        width = 100,
        height = 100;

var data = [{value: 10, dataset:"barbaz"},
{value: 40, dataset:"barbar"},
{value: -10, dataset:"foobaz"},
{value: 50, dataset:"foobar"},
];

// Add svg to
var svg = d3.select(d3Container.current)
.attr('class', 'root_netsentiment')
.append('svg').attr('width', width + margin.left + margin.right).attr('height', height + margin.top + margin.bottom).append('g').attr('transform', 'translate(' + margin.left + ',' + margin.top + ')');

// set the ranges
var y = d3.scaleBand()
    .range([height, 0])
    .padding(0.1);

var x = d3.scaleLinear()
    .range([0, width]);

// Scale the range of the data in the domains
x.domain(d3.extent(data, function (d) {
    return d.value;
}));
y.domain(data.map(function (d) {
    return d.dataset;
}));

// append the rectangles for the bar chart
svg.selectAll(".bar")
    .data(data)
    .enter().append("rect")
    .attr("class", function (d) {
        return "bar bar--" + (d.value < 0 ? "negative" : "positive");
    })
    .attr("x", function (d) {
        return x(Math.min(0, d.value));
    })
    .attr("y", function (d) {
        return y(d.dataset);
    })
    .attr("width", function (d) {
        return Math.abs(x(d.value) - x(0));
    })
    .attr("height", y.bandwidth());

// add the x Axis
svg.append("g")
    .attr("transform", "translate(0," + height + ")")
    .call(d3.axisBottom(x));

// add the y Axis
svg.append("g")
    .attr("class", "y axis")
    .attr("transform", "translate(" + x(0) + ",0)")
    .call(d3.axisRight(y));




    }) // close useeffect

    return(
        <>
            <svg id='netSentiment' width={200} height={200} ref={d3Container}></svg>

        </>
    )
}

export default NetSentiment
