                return "bar bar--" + (d.value < 0 ? "negative" : "positive");
            })
            .attr("x", function (d) {
                return x(Math.min(0, d.value));
            })
            .attr("y", function (d) {
                return y(d.name);
            })
            .attr("width", function (d) {
                return Math.abs(x(d.value) - x(0));
            })
            .attr("height", y.bandwidth());

        // add the x Axis
        svg.append("g")
