            .selectAll(".y.axis text")
            .style("fill","#4d4d4d")

       */ 

    }, [choosenSentiment, hover, useComment, useLike, useShare]) // close useeffect

    return(
        <>
        {/*
        <div className='btnContainer'>
          <button id='cha_button' className='groupButtonActive' onClick={()=>handleChangeData(choosenSentiment,'comments')}>Comments</button>
          <button id='cat_button' className='groupButtonInactive' onClick={()=>handleChangeData(choosenSentiment,'likes')}>Likes</button>
        </div>
        */}
            <div className='NetContainer'>
                <svg id='netSentiment' width={400} height={300} ref={d3Container}></svg> 
                <div className='textContainer'>
                    <div className='NetHeadline'>Net Sentiment =</div>
                    <div className='NetFormula'>(Positive sentiment - Negative sentiment) *<br/>
                                        1 x Comments * 1 x Likes * 1 x Shares
                    </div>
                    <div className='NetCheckboxes'>
                        <div className={useComment?'CheckboxItemActive':'CheckboxItemInactive'}>
                            <input type="checkbox" 
                                    name="foo" 
                                    value="com" 
                                    id="foo_com"
                                    checked={useComment}
                                    onChange={()=>setUseComment(!(useComment))}/>
                            <label className='checkLabel' for="foo_com">Comment</label>


                            <div className='NetWeights'>
                                {/*<button className='weightBtn'>-</button>*/}
                                <input className='weightInput' 
                                        type="number" 
                                        onChange={onChangeHandler}
                                        value={commentWeight}
                                        />

                            </div>
                        </div>

                        <div className={useLike?'CheckboxItemActive':'CheckboxItemInactive'}>
                            <input type="checkbox" 
                                    name="foo" 
                                    value="like" 
                                    id="foo_like"
                                    checked={useLike}
                                    onChange={()=>setUseLike(!(useLike))}/>
                            <label className='checkLabel' for="foo_like">Like</label>
                        </div>

                        <div className={useShare?'CheckboxItemActive':'CheckboxItemInactive'}>
                            <input type="checkbox" 
                                    name="foo" 
                                    value="share" 
                                    id="foo_share"
                                    checked={useShare}
                                    onChange={()=>setUseShare(!(useShare))}/>
                            <label className='checkLabel' for="foo_share">Share</label>
                        </div>
                    </div>


                </div>
            </div>


        </>
    )
}

export default NetSentiment
